        dataSource.insertWinCondition("Meenakshi Temple","0", "0", "0", "1", "0", "1");
        dataSource.insertWinCondition("Mont St. Michel","0", "1", "0", "1", "0", "1");
        dataSource.insertWinCondition("Oracle","0", "1", "0", "1", "0", "1");
